import React, { useState } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import GoldTable from "components/GoldTable";
import NotificationList from "components/NotificationList";
import CardGiftcardSharpIcon from "@material-ui/icons/CardGiftcardSharp";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Draggable from "react-draggable";

const DashboardView = (props) => {
  const { onHandleGetData } = props;
  const [hccid, setHccid] = useState("");
  const [open, setOpen] = React.useState(false);
  // this.state={
  //   hccid:0,
  //   name:''
  // }
  const handleClickOpen = (event) => {
    event.preventDefault();
    onHandleGetData(hccid);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  return (
    <>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <div>
          <Avatar>
            <CardGiftcardSharpIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <form  onSubmit={handleClickOpen}>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="HCCID"
              label="HCCID"
              name="HCCID"
              value={hccid}
              onChange={(e) => {
                setHccid(e.target.value);
              }}
              autoFocus
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="fullname"
              label="Fullname"
              id="fullname"
              // value={this.state.name}
            />

            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              onClick={handleClickOpen}
            >
              Get Prize
            </Button>
            <Dialog
              open={open}
              onClose={handleClose}
              // PaperComponent={PaperComponent}
            >
              <DialogTitle
                style={{ cursor: "move" }}
                id="draggable-dialog-title"
              >
                Subscribe
              </DialogTitle>
              <DialogContent>
                <DialogContentText>
                  To subscribe to this website, please enter your email address
                  here. We will send updates occasionally.
                </DialogContentText>
              </DialogContent>
              <DialogActions>
                <Button autoFocus onClick={handleClose} color="primary">
                  Cancel
                </Button>
                <Button onClick={handleClose} color="primary">
                  Subscribe
                </Button>
              </DialogActions>
            </Dialog>
          </form>
        </div>
        <Box mt={8}></Box>
      </Container>
      <Container maxWidth="lg">
        <Grid container spacing={3}>
          {/* GoldTable */}
          <Grid item xs={12} md={12} lg={12}>
            <Paper>
              <GoldTable />
            </Paper>
          </Grid>
          {/* <Grid item xs={12} md={4} lg={3}>
            <Paper>
              <NotificationList />
            </Paper>
          </Grid> */}
        </Grid>
      </Container>
    </>
  );
};
// function PaperComponent(props) {
//   return (
//     <Draggable
//       handle="#draggable-dialog-title"
//       cancel={'[class*="MuiDialogContent-root"]'}
//     >
//       <Paper {...props} />
//     </Draggable>
//   );
// }
export default DashboardView;
